import './Weather.css';
import Footer from './Footer'
import Header from './Header'



function Weather() {
    var lat,lon;

    navigator.geolocation.getCurrentPosition((position) => {
        // document.getElementById("temp").innerHTML = position.coords.latitude+" "+position.coords.longitude;
        lat=position.coords.latitude;
        lon=position.coords.longitude;
        const url=`https://api.openweathermap.org/data/3.0/onecall?lat=${lat}&lon=${lon}&appid=618a4292522a19670669f6891782444f`;
        "https://api.openweathermap.org/data/2.5/weather?q={city name}&appid=618a4292522a19670669f6891782444f";
        document.getElementById("box").innerHTML = url;
    });


    // console.log({url});
    return (
        <>
            <Header/>
            <div className='weather'>
                <div className='heading'>
                    Weather
                </div>
                <div className='box' id='box'>
                    <div className='card1 purple' id='temp'></div>
                </div>
            </div>
            <Footer/>
        </>

    );
}
